mapper.insert:
INSERT INTO tb_opertion_info(

     id
     ,opertion_code
     ,opertion_sort
     ,opertion_name
     ,opertion_condition
     ,opertion_flow_score
     ,opertion_skill_score
     ,opertion_type
     ,creater_id
     ,created_at
     ,updated_at
     ,time_stamp

) VALUES (

     NULL
     ,#{opertionCode}
     ,#{opertionSort}
     ,#{opertionName}
     ,#{opertionCondition}
     ,#{opertionFlowScore}
     ,#{opertionSkillScore}
     ,#{opertionType}
     ,#{createrId}
     ,#{createdAt}
     ,#{updatedAt}
     ,#{timeStamp}

)

SELECT LAST_INSERT_ID() AS id

