opertion_code = #{opertionCode}, opertion_sort = #{opertionSort}, opertion_name = #{opertionName}, opertion_condition = #{opertionCondition}, opertion_flow_score = #{opertionFlowScore}, opertion_skill_score = #{opertionSkillScore}, opertion_type = #{opertionType}, creater_id = #{createrId}, created_at = #{createdAt}, updated_at = #{updatedAt}, time_stamp = #{timeStamp}
