         AND opertion_code not in (${_or_not_in.opertionCode.str})


         AND opertion_sort like '%${_or_like.opertionSort.like}%'


          AND opertion_sort = ${_or.opertionSort.str}


         AND opertion_sort in (${_or_in.opertionSort.str})


         AND opertion_sort not in (${_or_not_in.opertionSort.str})


         AND opertion_name like '%${_or_like.opertionName.like}%'


          AND opertion_name = ${_or.opertionName.str}


         AND opertion_name in (${_or_in.opertionName.str})


         AND opertion_name not in (${_or_not_in.opertionName.str})


         AND opertion_condition like '%${_or_like.opertionCondition.like}%'


          AND opertion_condition = ${_or.opertionCondition.str}


         AND opertion_condition in (${_or_in.opertionCondition.str})


         AND opertion_condition not in (${_or_not_in.opertionCondition.str})


         AND opertion_flow_score like '%${_or_like.opertionFlowScore.like}%'


          AND opertion_flow_score = ${_or.opertionFlowScore.str}


         AND opertion_flow_score in (${_or_in.opertionFlowScore.str})


         AND opertion_flow_score not in (${_or_not_in.opertionFlowScore.str})


         AND opertion_skill_score like '%${_or_like.opertionSkillScore.like}%'


          AND opertion_skill_score = ${_or.opertionSkillScore.str}


         AND opertion_skill_score in (${_or_in.opertionSkillScore.str})


         AND opertion_skill_score not in (${_or_not_in.opertionSkillScore.str})


         AND opertion_type like '%${_or_like.opertionType.like}%'


          AND opertion_type = ${_or.opertionType.str}


         AND opertion_type in (${_or_in.opertionType.str})


         AND opertion_type not in (${_or_not_in.opertionType.str})


         AND creater_id like '%${_or_like.createrId.like}%'


          AND creater_id = ${_or.createrId.str}


         AND creater_id in (${_or_in.createrId.str})


         AND creater_id not in (${_or_not_in.createrId.str})


         AND created_at like '%${_or_like.createdAt.like}%'


          AND created_at = ${_or.createdAt.str}


         AND created_at in (${_or_in.createdAt.str})


         AND created_at not in (${_or_not_in.createdAt.str})


         AND updated_at like '%${_or_like.updatedAt.like}%'


          AND updated_at = ${_or.updatedAt.str}


         AND updated_at in (${_or_in.updatedAt.str})


         AND updated_at not in (${_or_not_in.updatedAt.str})


         AND time_stamp like '%${_or_like.timeStamp.like}%'


          AND time_stamp = ${_or.timeStamp.str}


         AND time_stamp in (${_or_in.timeStamp.str})


         AND time_stamp not in (${_or_not_in.timeStamp.str})

     )

      AND 
        1=1


          AND id is null


          AND opertion_code is null


          AND opertion_sort is null


          AND opertion_name is null


          AND opertion_condition is null


          AND opertion_flow_score is null


          AND opertion_skill_score is null


          AND opertion_type is null


          AND creater_id is null


          AND created_at is null


          AND updated_at is null


          AND time_stamp is null


      AND 
        1=1


          AND id is not null


          AND opertion_code is not null


          AND opertion_sort is not null


          AND opertion_name is not null


          AND opertion_condition is not null


          AND opertion_flow_score is not null


          AND opertion_skill_score is not null


          AND opertion_type is not null


          AND creater_id is not null


          AND created_at is not null


          AND updated_at is not null


          AND time_stamp is not null




ORDER BY


    id ${order.order}
    opertion_code ${order.order}
    opertion_sort ${order.order}
    opertion_name ${order.order}
    opertion_condition ${order.order}
    opertion_flow_score ${order.order}
    opertion_skill_score ${order.order}
    opertion_type ${order.order}
    creater_id ${order.order}
    created_at ${order.order}
    updated_at ${order.order}
    time_stamp ${order.order}



 LIMIT #{pageStart},#{pageSize}
