mapper.insert:
INSERT INTO tb_role_info(

     id
     ,role_name
     ,role_mark
     ,creater_id
     ,created_at
     ,updated_at
     ,time_stamp

) VALUES (

     NULL
     ,#{roleName}
     ,#{roleMark}
     ,#{createrId}
     ,#{createdAt}
     ,#{updatedAt}
     ,#{timeStamp}

)

SELECT LAST_INSERT_ID() AS id

mapper.update:
UPDATE tb_role_info
role_name = #{roleName}, role_mark = #{roleMark}, creater_id = #{createrId}, created_at = #{createdAt}, updated_at = #{updatedAt}, time_stamp = #{timeStamp}
WHERE id = #{id} LIMIT 1

