         AND creater_id not in (${_notin.createrId.str})

       AND created_at = #{createdAt}

         AND created_at >=> #{_range.createdAt.min}
         AND created_at <=< #{_range.createdAt.max}


         AND created_at like '%${_like.createdAt.like}%'


         AND created_at in (${_in.createdAt.str})


         AND created_at not in (${_notin.createdAt.str})

       AND updated_at = #{updatedAt}

         AND updated_at >=> #{_range.updatedAt.min}
         AND updated_at <=< #{_range.updatedAt.max}


         AND updated_at like '%${_like.updatedAt.like}%'


         AND updated_at in (${_in.updatedAt.str})


         AND updated_at not in (${_notin.updatedAt.str})

       AND time_stamp = #{timeStamp}

         AND time_stamp >=> #{_range.timeStamp.min}
         AND time_stamp <=< #{_range.timeStamp.max}


         AND time_stamp like '%${_like.timeStamp.like}%'


         AND time_stamp in (${_in.timeStamp.str})


         AND time_stamp not in (${_notin.timeStamp.str})

      OR (
        1=1


         AND id like '%${_or_like.id.like}%'


          AND id = ${_or.id.str}


         AND id in (${_or_in.id.str})


         AND id not in (${_or_not_in.id.str})


         AND role_name like '%${_or_like.roleName.like}%'


          AND role_name = ${_or.roleName.str}


         AND role_name in (${_or_in.roleName.str})


         AND role_name not in (${_or_not_in.roleName.str})


         AND role_mark like '%${_or_like.roleMark.like}%'


          AND role_mark = ${_or.roleMark.str}


         AND role_mark in (${_or_in.roleMark.str})


         AND role_mark not in (${_or_not_in.roleMark.str})


         AND creater_id like '%${_or_like.createrId.like}%'


          AND creater_id = ${_or.createrId.str}


         AND creater_id in (${_or_in.createrId.str})


         AND creater_id not in (${_or_not_in.createrId.str})


         AND created_at like '%${_or_like.createdAt.like}%'


          AND created_at = ${_or.createdAt.str}


         AND created_at in (${_or_in.createdAt.str})


         AND created_at not in (${_or_not_in.createdAt.str})


         AND updated_at like '%${_or_like.updatedAt.like}%'


          AND updated_at = ${_or.updatedAt.str}


         AND updated_at in (${_or_in.updatedAt.str})


         AND updated_at not in (${_or_not_in.updatedAt.str})


         AND time_stamp like '%${_or_like.timeStamp.like}%'


          AND time_stamp = ${_or.timeStamp.str}


         AND time_stamp in (${_or_in.timeStamp.str})


         AND time_stamp not in (${_or_not_in.timeStamp.str})

     )

      AND 
        1=1


          AND id is null


          AND role_name is null


          AND role_mark is null


          AND creater_id is null


          AND created_at is null


          AND updated_at is null


          AND time_stamp is null


      AND 
        1=1


          AND id is not null


          AND role_name is not null


          AND role_mark is not null


          AND creater_id is not null


          AND created_at is not null


          AND updated_at is not null


          AND time_stamp is not null




ORDER BY


    id ${order.order}
    role_name ${order.order}
    role_mark ${order.order}
    creater_id ${order.order}
    created_at ${order.order}
    updated_at ${order.order}
    time_stamp ${order.order}



 LIMIT #{pageStart},#{pageSize}
