mapper.insert:
INSERT INTO tb_role_module(

     id
     ,role_id
     ,module_id
     ,module_cn_name
     ,module_en_name
     ,module_route
     ,module_icon
     ,parent_id
     ,module_status
     ,module_type
     ,module_sort
     ,creater_id
     ,created_at
     ,updated_at
     ,time_stamp

) VALUES (

     NULL
     ,#{roleId}
     ,#{moduleId}
     ,#{moduleCnName}
     ,#{moduleEnName}
     ,#{moduleRoute}
     ,#{moduleIcon}
     ,#{parentId}
     ,#{moduleStatus}
     ,#{moduleType}
     ,#{moduleSort}
     ,#{createrId}
     ,#{createdAt}
     ,#{updatedAt}
     ,#{timeStamp}

)

SELECT LAST_INSERT_ID() AS id

mapper.update:
